      //   <form className="ui form">
      //     <div className="field">
      //       <input
      //         id="Email"
      //         id="inp"
      //         placeholder="Email"
      //         onChange={this.handleChangeEmail}
      //       />
      //     </div>
      //     <br />
      //     <div className="field">
      //       <input
      //         id="password"
      //         id="inp"
      //         placeholder="password"
      //         onChange={this.handleChangepassword}
      //       />
      //     </div>
      //     <div className="field">
      //       <div className="ui checkbox">
      //         <input
      //           type="checkbox"
      //           id="h1"
      //           className="hidden"
      //           readOnly=""
      //           tabIndex="0"
      //         />
      //         <label id="h2">remember me</label>
      //       </div>
      //     </div>
      //     <br />
      //     <button
      //       type="submit"
      //       id="inp"
      //       onClick={this.handleSubmit}
      //       className="ui button"
      //     >
      //       login
      //     </button>
      //   </form>
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <br />
      //   <Footer />
      // </div><div>
      <div>
        <NavU />
        <Grid
          textAlign="center"
          style={{ height: "100vh" }}
          verticalAlign="middle"
        >
          <Grid.Column style={{ maxWidth: 450 }}>
            <div>Login to your account </div>
            <Form size="large">
              {/* <Form size='large' onSubmit={this.handleSubmit}> */}
              <Segment stacked>
                <Form.Input
                  name="Email"
                  fluid
                  icon="phone"
                  iconPosition="left"
                  placeholder="+970"
                  // type='number'
                  value={this.state.Email}
                  onChange={this.handleChangeEmail}
                />
                <Form.Input
                  name="password"
                  className="input"
                  fluid
                  icon="lock"
                  iconPosition="left"
                  placeholder="Password"
                  type="password"
                  value={this.state.password}
                  onChange={this.handleChangepassword}
                />

                <Button
                  className="butLog"
                  fluid
                  size="large"
                  type="submit"
                  onClick={this.handleSubmit}
                >
                  Login
                </Button>
              </Segment>
            </Form>
            <Message className="gray">
              <Link className="white" to={"/UserForm"}>
                Sign Up
              </Link>
            </Message>
          </Grid.Column>
        </Grid>
        <Footer />
      </div>
    );
  }
}
export default Login;
